import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import "./styles.css";

const NUM_ROWS = 4;
const NUM_COLS = 3;
const boardImage = "/img/board1.png"; // ✅ ตรวจสอบ path ให้ถูกต้อง

useEffect(() => {
  if (boardRef.current) {
    const rect = boardRef.current.getBoundingClientRect();

    // ✅ กำหนดตำแหน่งเป้าหมายบนกระดาน
    const newTargets = Array.from({ length: NUM_ROWS * NUM_COLS }, (_, index) => ({
      id: index,
      targetX: rect.left + (index % NUM_COLS) * (rect.width / NUM_COLS),
      targetY: rect.top + Math.floor(index / NUM_COLS) * (rect.height / NUM_ROWS),
    }));

    // ✅ สุ่มตำแหน่งให้ชิ้นส่วนจิ๊กซอว์กองอยู่ฝั่งซ้ายของกระดาน
    const newPieces = newTargets.map((target, index) => ({
      ...target,
      initialX: rect.left - rect.width * 0.7 + Math.random() * 50, // 🔥 เลื่อนชิ้นส่วนไปทางซ้ายของกระดาน
      initialY: rect.top + Math.random() * (rect.height - 100), // 🔥 กระจาย Y แต่ไม่ให้ออกนอกจอ
      image: `/img/puzzle/piece${target.id + 1}.png`, // ✅ รูปจิ๊กซอว์แต่ละชิ้น
      width: rect.width / NUM_COLS,
      height: rect.height / NUM_ROWS,
    }));

    setTargets(newTargets);
    setPieces(newPieces);
  }
}, []);


export default function App() {
  const boardRef = useRef(null);
  const [pieces, setPieces] = useState([]);
  const [targets, setTargets] = useState([]);
  const [completed, setCompleted] = useState([]);

  useEffect(() => {
    const updatePositions = () => {
      if (boardRef.current) {
        const rect = boardRef.current.getBoundingClientRect();

        const newTargets = Array.from({ length: NUM_ROWS * NUM_COLS }, (_, index) => ({
          id: index,
          targetX: rect.left + (index % NUM_COLS) * (rect.width / NUM_COLS),
          targetY: rect.top + Math.floor(index / NUM_COLS) * (rect.height / NUM_ROWS),
        }));

        const newPieces = newTargets.map((target, index) => ({
          ...target,
          initialX: rect.left - 300 + (index % NUM_COLS) * 50, // ✅ กระจายชิ้นส่วนด้านซ้าย
          initialY: rect.top + Math.random() * rect.height * 0.5,
          image: `/img/puzzle/piece${target.id + 1}.png`, // ✅ ตรวจสอบ path
          width: rect.width / NUM_COLS,
          height: rect.height / NUM_ROWS,
        }));

        setTargets(newTargets);
        setPieces(newPieces);
      }
    };

    setTimeout(updatePositions, 500); // ✅ ป้องกัน boardRef.current ยังไม่มีค่า
    window.addEventListener("resize", updatePositions);
    return () => window.removeEventListener("resize", updatePositions);
  }, []);

  const handleDragEnd = (event, info, piece) => {
    const target = targets.find((t) => t.id === piece.id);

    if (
      target &&
      Math.abs(info.point.x - target.targetX) < piece.width / 2 &&
      Math.abs(info.point.y - target.targetY) < piece.height / 2
    ) {
      setCompleted((prev) => [...prev, piece.id]);
      setPieces((prev) =>
        prev.map((p) => (p.id === piece.id ? { ...p, initialX: target.targetX, initialY: target.targetY } : p))
      );
    }
  };

  return (
    <div className="board-container">
      <div
        className="board"
        ref={boardRef}
        style={{
          width: "600px", // ✅ กำหนดขนาดชัดเจน
          height: "800px",
          backgroundImage: `url(${boardImage})`,
          backgroundSize: "contain",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
        }}
      />

      {pieces.map((piece) => (
        <motion.div
          key={piece.id}
          className="piece"
          style={{
            position: "absolute",
            cursor: "grab",
            width: piece.width,
            height: piece.height,
          }}
          drag={!completed.includes(piece.id)}
          dragElastic={0.5}
          initial={{ x: piece.initialX, y: piece.initialY }}
          animate={{ x: piece.initialX, y: piece.initialY }}
          onPointerDown={(event) => event.preventDefault()}
          onDragStart={(event) => event.preventDefault()}
          onDragEnd={(event, info) => handleDragEnd(event, info, piece)}
        >
          <img
            src={piece.image}
            alt={`piece-${piece.id}`}
            style={{
              width: "100%",
              height: "100%",
              objectFit: "contain",
            }}
          />
        </motion.div>
      ))}
    </div>
  );
}
