import React, { useState } from "react";
import { motion } from "framer-motion";
import "./styles.css";

const NUM_ROWS = 4; // จำนวนแถวของกรอบเป้าหมาย
const NUM_COLS = 3; // จำนวนคอลัมน์ของกรอบเป้าหมาย

const createGrid = () =>
  Array.from({ length: NUM_ROWS * NUM_COLS }, (_, index) => ({
    id: index,
    targetX: (index % NUM_COLS) * 100, // ตำแหน่ง X ของเป้าหมาย
    targetY: Math.floor(index / NUM_COLS) * 100, // ตำแหน่ง Y ของเป้าหมาย
    initialX: 400 + Math.random() * 50, // ตำแหน่งเริ่มต้น (มุมขวาล่าง)
    initialY: 250 + Math.random() * 50,
    image: `/img/puzzle/piece${index + 1}.png`, // ใช้ไฟล์ .svg
    width: 100,
    height:100
  }));

export default function App() {
  const [pieces, setPieces] = useState(createGrid());
  const [completed, setCompleted] = useState([]);

  // ฟังก์ชันที่ใช้ตรวจสอบตำแหน่งและการลาก
  const handleDragEnd = (event, info, piece) => {
    const target = pieces.find((p) => p.id === piece.id);

    // ตรวจสอบว่าชิ้นถูกลากไปใกล้ตำแหน่งเป้าหมายหรือไม่
    if (
      Math.abs(info.point.x - target.targetX) < 50 &&
      Math.abs(info.point.y - target.targetY) < 50
    ) {
      // หากเข้าใกล้กรอบเป้าหมายมากพอแล้ว ล็อคตำแหน่ง
      setCompleted((prev) => [...prev, piece.id]);
    }
  };

  return (
    <div className="board">
      {/* กรอบเป้าหมาย */}
      {pieces.map((piece) => (
        <div
          key={piece.id}
          className="target"
          style={{
            left: piece.targetX,
            top: piece.targetY,
            width: "100px",
            height: "100px",
            position: "absolute", // กำหนดตำแหน่งให้ถูกต้อง
            border: "1px dashed #ccc", // กรอบเป้าหมาย
          }}
        />
      ))}

      {/* ชิ้นจิ๊กซอว์ */}
      {pieces.map(
        (piece) =>
          !completed.includes(piece.id) && (
            <motion.div
              key={piece.id}
              className="piece"
              style={{
                position: "absolute", // กำหนดตำแหน่งให้ถูกต้อง
                cursor: "grab",
              }}
              drag
              dragElastic={0.5}
              initial={{ x: piece.initialX, y: piece.initialY }}
              animate={
                completed.includes(piece.id)
                  ? { x: piece.targetX, y: piece.targetY }
                  : undefined
              }
              onDragEnd={(event, info) => handleDragEnd(event, info, piece)}
            >
              <img
                src={piece.image}
                alt={`piece-${piece.id}`}
                style={{
                  width: "100px",
                  height: "100px",
                }}
              />
            </motion.div>
          )
      )}
    </div>
  );
}
