import React, { useState } from "react";
import { motion } from "framer-motion";
import "./styles.css";

const NUM_ROWS = 4;
const NUM_COLS = 3;
const PIECE_SIZE = 80; // à¸‚à¸™à¸²à¸”à¸Šà¸´à¹‰à¸™à¸ˆà¸´à¹Šà¸à¸‹à¸­à¸§à¹Œà¸—à¸µà¹ˆà¹€à¸¥à¹‡à¸à¸¥à¸‡

const createGrid = () =>
  Array.from({ length: NUM_ROWS * NUM_COLS }, (_, index) => ({
    id: index,
    targetX: (index % NUM_COLS) * PIECE_SIZE,
    targetY: Math.floor(index / NUM_COLS) * PIECE_SIZE,
    initialX: 400 + Math.random() * 50,
    initialY: 250 + Math.random() * 50,
    locked: false, // ðŸ”¥ à¹€à¸žà¸´à¹ˆà¸¡à¸•à¸±à¸§à¹à¸›à¸£à¹€à¸žà¸·à¹ˆà¸­à¹€à¸Šà¹‡à¸„à¸§à¹ˆà¸²à¸–à¸¹à¸à¸¥à¹‡à¸­à¸„à¸«à¸£à¸·à¸­à¸¢à¸±à¸‡
    image: `/img/puzzle/piece${index + 1}.png`,
  }));

export default function App() {
  const [pieces, setPieces] = useState(createGrid());

  const handleDragEnd = (event, info, piece) => {
    const target = pieces.find((p) => p.id === piece.id);

    console.log(`ðŸŸ¢ Piece ${piece.id} Dragged to:`, {
      draggedX: info.point.x,
      draggedY: info.point.y,
      targetX: target.targetX,
      targetY: target.targetY,
    });

    if (
      Math.abs(info.point.x - target.targetX) < PIECE_SIZE * 0.75 &&
      Math.abs(info.point.y - target.targetY) < PIECE_SIZE * 0.75
    )
     {
      setPieces((prev) =>
        prev.map((p) =>
          p.id === piece.id
            ? { ...p, initialX: target.targetX, initialY: target.targetY, locked: true }
            : p
        )
      );
    }
  };

  return (
    <div className="board" style={{ position: "relative", width: "100vw", height: "100vh" }}>
      {/* à¸à¸£à¸­à¸šà¹€à¸›à¹‰à¸²à¸«à¸¡à¸²à¸¢ */}
      {pieces.map((piece) => (
        <div
          key={`target-${piece.id}`}
          className="target"
          style={{
            left: piece.targetX,
            top: piece.targetY,
            width: PIECE_SIZE,
            height: PIECE_SIZE,
            position: "absolute",
            border: "2px dashed #ccc",
          }}
        />
      ))}

      {/* à¸Šà¸´à¹‰à¸™à¸ˆà¸´à¹Šà¸à¸‹à¸­à¸§à¹Œ */}
      {pieces.map((piece) => (
        <motion.div
          key={`piece-${piece.id}`}
          className="piece"
          style={{
            position: "absolute",
            cursor: piece.locked ? "default" : "grab",
          }}
          drag={!piece.locked} // âœ… à¸¥à¹‡à¸­à¸„à¸Šà¸´à¹‰à¸™à¸ªà¹ˆà¸§à¸™à¸–à¹‰à¸²à¸¥à¸²à¸à¹„à¸›à¸§à¸²à¸‡à¹à¸¥à¹‰à¸§
          dragElastic={0.5}
          initial={{ x: piece.initialX, y: piece.initialY }}
          animate={{ x: piece.initialX, y: piece.initialY }} // âœ… à¹ƒà¸Šà¹‰à¸„à¹ˆà¸²à¸—à¸µà¹ˆà¸­à¸±à¸›à¹€à¸”à¸•à¸¥à¹ˆà¸²à¸ªà¸¸à¸”
          onPointerDown={(event) => event.preventDefault()} // à¸›à¹‰à¸­à¸‡à¸à¸±à¸™ Safari à¸¥à¸²à¸à¸£à¸¹à¸›à¸­à¸­à¸
          onDragStart={(event) => event.preventDefault()} // à¸›à¹‰à¸­à¸‡à¸à¸±à¸™à¸à¸²à¸£à¸”à¸¶à¸‡à¸£à¸¹à¸›à¸­à¸­à¸à¸ˆà¸²à¸à¹€à¸§à¹‡à¸š
          onDragEnd={(event, info) => handleDragEnd(event, info, piece)}
        >
          <img
            src={piece.image}
            alt={`piece-${piece.id}`}
            style={{
              width: `${PIECE_SIZE}px`, // âœ… à¸à¸³à¸«à¸™à¸”à¸‚à¸™à¸²à¸”à¹€à¸›à¹‡à¸™à¸ªà¸±à¸”à¸ªà¹ˆà¸§à¸™
              height: `${PIECE_SIZE}px`,
              objectFit: "contain",
            }}
          />
        </motion.div>
      ))}
    </div>
  );
}
