import React from "react";
import { useState } from "react";
import { motion, AnimateSharedLayout } from "framer-motion";
import { useViewportWidth } from "./use-viewport-width";
import "./styles.css";

/**
 * NOTICE: This won't work in later versions of Framer Motion. The
 * shared drag gesture was never widely used. But we may add it back
 * some time in the future.
 *
 * This is an example of a shared drag gesture in Framer Motion 2.
 *
 * When the box is dragged from one half to the other, it gets
 * removed from one component and added in the other. This isn't
 * the same box - it is two seperate components that share a layoutId.
 */

export default function App() {
  const viewportWidth = useViewportWidth();
  const [activeHalf, setActiveHalf] = useState("a");

  const [pieces, setPieces] = useState(createGrid());
const [completed, setCompleted] = useState([]);
const NUM_ROWS = 3; // จำนวนแถว
const NUM_COLS = 4; // จำนวนคอลัมน์
const createGrid = () =>
  Array.from({ length: NUM_ROWS * NUM_COLS }, (_, index) => ({
    id: index,
    x: (index % NUM_COLS) * 100, // คำนวณตำแหน่งแนวนอน
    y: Math.floor(index / NUM_COLS) * 100, // คำนวณตำแหน่งแนวตั้ง
  }));



  const onViewportBoxUpdate = ({ x }) => {
    const halfViewport = viewportWidth.current / 2;

    if (activeHalf === "a" && x.min > halfViewport) {
      setActiveHalf("b");
    } else if (activeHalf === "b" && x.max < halfViewport) {
      setActiveHalf("a");
    }
  };

  return (
    <AnimateSharedLayout>
      <div className="container">
        <Zone
          color="#f107a3"
          isSelected={activeHalf === "a"}
          onViewportBoxUpdate={onViewportBoxUpdate}
        />
        <Zone
          color="#7b2ff7"
          isSelected={activeHalf === "b"}
          onViewportBoxUpdate={onViewportBoxUpdate}
        />
      </div>
    </AnimateSharedLayout>
  );
}

