import React, { useState } from "react";
import { motion } from "framer-motion";
import "./styles.css";

const NUM_ROWS = 4; // ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡πÅ‡∏ñ‡∏ß‡∏Ç‡∏≠‡∏á‡∏Å‡∏£‡∏≠‡∏ö‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢
const NUM_COLS = 3; // ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ñ‡∏≠‡∏•‡∏±‡∏°‡∏ô‡πå‡∏Ç‡∏≠‡∏á‡∏Å‡∏£‡∏≠‡∏ö‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢

const createGrid = () =>
  Array.from({ length: NUM_ROWS * NUM_COLS }, (_, index) => ({
    id: index,
    targetX: (index % NUM_COLS) * 100, // ‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á X ‡∏Ç‡∏≠‡∏á‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢
    targetY: Math.floor(index / NUM_COLS) * 100, // ‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á Y ‡∏Ç‡∏≠‡∏á‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢
    initialX: 400 + Math.random() * 50, // ‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏ï‡πâ‡∏ô (‡∏°‡∏∏‡∏°‡∏Ç‡∏ß‡∏≤‡∏•‡πà‡∏≤‡∏á)
    initialY: 250 + Math.random() * 50,
    image: `/img/puzzle/piece${index + 1}.png`, // ‡πÉ‡∏ä‡πâ‡πÑ‡∏ü‡∏•‡πå .svg

  }));

export default function App() {
  const [pieces, setPieces] = useState(createGrid());
  const [completed, setCompleted] = useState([]);

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÅ‡∏•‡∏∞‡∏Å‡∏≤‡∏£‡∏•‡∏≤‡∏Å
  const handleDragEnd = (event, info, piece) => {
    const target = pieces.find((p) => p.id === piece.id);

    // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏ß‡πà‡∏≤‡∏ä‡∏¥‡πâ‡∏ô‡∏ñ‡∏π‡∏Å‡∏•‡∏≤‡∏Å‡πÑ‡∏õ‡πÉ‡∏Å‡∏•‡πâ‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà
    if (
      Math.abs(info.point.x - target.targetX) < 50 &&
      Math.abs(info.point.y - target.targetY) < 50
    ) {
      // ‡∏´‡∏≤‡∏Å‡πÄ‡∏Ç‡πâ‡∏≤‡πÉ‡∏Å‡∏•‡πâ‡∏Å‡∏£‡∏≠‡∏ö‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢‡∏°‡∏≤‡∏Å‡∏û‡∏≠‡πÅ‡∏•‡πâ‡∏ß ‡∏•‡πá‡∏≠‡∏Ñ‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á
      setCompleted((prev) => [...prev, piece.id]);
    }
  };

  return (
    <div className="board">
      {/* ‡∏Å‡∏£‡∏≠‡∏ö‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢ */}
      {pieces.map((piece) => (
        <div
          key={piece.id}
          className="target"
          style={{
            left: piece.targetX,
            top: piece.targetY,
            width: "100px",
            height: "100px",
            position: "absolute", // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á
            border: "1px dashed #ccc", // ‡∏Å‡∏£‡∏≠‡∏ö‡πÄ‡∏õ‡πâ‡∏≤‡∏´‡∏°‡∏≤‡∏¢
          }}
        />
      ))}

      {/* ‡∏ä‡∏¥‡πâ‡∏ô‡∏à‡∏¥‡πä‡∏Å‡∏ã‡∏≠‡∏ß‡πå */}
      {pieces.map(
        (piece) =>
          !completed.includes(piece.id) && (
            <motion.div
              key={piece.id}
              className="piece"
              style={{
                position: "absolute", // ‡∏Å‡∏≥‡∏´‡∏ô‡∏î‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á
                cursor: "grab",
              }}
              //  draggable="false"

              drag
              dragElastic={0.5}
              initial={{ x: piece.initialX, y: piece.initialY }}
              animate={
                completed.includes(piece.id)
                  ? { x: piece.targetX, y: piece.targetY }
                  : undefined
              }
              onPointerDown={(event) => event.preventDefault()}  // üî• ‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô Safari ‡πÅ‡∏•‡∏∞ Chrome ‡∏•‡∏≤‡∏Å‡∏£‡∏π‡∏õ‡∏≠‡∏≠‡∏Å

              onDragStart={(event) => event.preventDefault()}  // üî• ‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡∏Å‡∏≤‡∏£‡∏î‡∏∂‡∏á‡∏£‡∏π‡∏õ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡πÄ‡∏ß‡πá‡∏ö
              onDragEnd={(event, info) => handleDragEnd(event, info, piece)}            >
              <img
                src={piece.image}
                alt={`piece-${piece.id}`}
                style={{
                  width: "100px",
                  height: "100px",
                }}
              />
            </motion.div>
          )
      )}
    </div>
  );
}
